import 'react'
import 'react-calendar/dist/Calendar.css';

function CadastroHorario() {
    document.addEventListener('DOMContentLoaded', function () {
        const eventos = [];
        const listaEventos = document.getElementById('lista-eventos');

        const calendario = document.getElementById('calendario');
        const meses = [
          'Janeiro', 'Fevereiro', 'Março', 'Abril', 'Maio', 'Junho',
          'Julho', 'Agosto', 'Setembro', 'Outubro', 'Novembro', 'Dezembro'
        ];

        function renderizarCalendario(ano, mes) {
          calendario.innerHTML = '';

          const diasNoMes = new Date(ano, mes + 1, 0).getDate();
          const primeiroDiaSemana = new Date(ano, mes, 1).getDay();

          const header = document.createElement('div');
          header.classList.add('header');
          header.textContent = `${meses[mes]} ${ano}`;
          calendario.appendChild(header);

          for (let i = 0; i < diasNoMes; i++) {
            const dia = document.createElement('div');
            dia.classList.add('dia');
            dia.textContent = i + 1;

            const eventosDoDia = eventos.filter(evento => {
              const dataEvento = new Date(evento.date);
              return (
                dataEvento.getFullYear() === ano &&
                dataEvento.getMonth() === mes &&
                dataEvento.getDate() === i + 1
              );
            });

            if (eventosDoDia.length > 0) {
              dia.classList.add('evento');
              dia.setAttribute('data-eventos', eventosDoDia.map(e => e.title).join(', '));
              dia.addEventListener('click', exibirEventosDoDia);
            }

            calendario.appendChild(dia);
          }
        }

        function exibirEventosDoDia(event) {
          alert(`Eventos do dia ${event.target.textContent}:\n${event.target.getAttribute('data-eventos')}`);
        }

        function atualizarListaEventos() {
          listaEventos.innerHTML = '';

          eventos.forEach(evento => {
            const li = document.createElement('li');
            li.textContent = `${evento.title} - ${evento.date}`;
            listaEventos.appendChild(li);
          });
        }

        function adicionarEvento() {
          const titulo = prompt('Digite o título do evento:');
          if (titulo) {
            const data = prompt('Digite a data do evento (YYYY-MM-DD):');
            if (data && validarData(data)) {
              const novoEvento = {
                title: titulo,
                date: data,
              };

              eventos.push(novoEvento);
              atualizarListaEventos();
              renderizarCalendario(new Date().getFullYear(), new Date().getMonth());
            } else {
              alert('Formato de data inválido. Use o formato YYYY-MM-DD.');
            }
          }
        }

        function validarData(data) {
          const regexData = /^\d{4}-\d{2}-\d{2}$/;
          return regexData.test(data);
        }

        renderizarCalendario(new Date().getFullYear(), new Date().getMonth());

        document.getElementById('calendario').addEventListener('click', function () {
          adicionarEvento();
        });
      });


}

export default CadastroHorario;
