import React, { useState, useEffect } from 'react';
import CadastroHorario from './CadHorario';

function Inicio() {
  const [horario, setHorario] = useState([]);

  useEffect(() => {
    // Recupera os dados do localStorage
    const storedHorario = JSON.parse(localStorage.getItem('horario')) || [];
    setHorario(storedHorario);
  }, []);

  const diasDoMes = Array.from({ length: 30 }, (_, index) => index + 1);

  return (
    <div>
      <h2>Novembro de 2023</h2>
      <table>
        <thead>
          <tr>
            <th>Dom</th>
            <th>Seg</th>
            <th>Ter</th>
            <th>Qua</th>
            <th>Qui</th>
            <th>Sex</th>
            <th>Sáb</th>
          </tr>
        </thead>
        <tbody>
          {chunkArray(diasDoMes, 7).map((semana, index) => (
            <tr key={index}>
              {semana.map((dia, diaIndex) => (
                <td key={diaIndex}>
                  {dia}
                  <ul>
                    {horario
                      .filter((horario) => horario.diaDaSemana === getDiaSemana(diaIndex))
                      .map((horarioItem) => (
                        <li key={horarioItem.id}>
                          <td>{horarioItem.id}</td>
                          <td>{horarioItem.professor}</td>
                          <td>{horarioItem.sala}</td>
                          <td>{horarioItem.desafio}</td>
                          <td>{horarioItem.horaInicial} a{' '}
                            {horarioItem.horaFinal}
                          </td>
                        </li>
                      ))}
                  </ul>
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
      {/* Adiciona o componente CadastroHorario */}
      <CadastroHorario />
    </div>
  );
}

function getDiaSemana(index) {
  const diasSemana = ['domingo', 'segunda-feira', 'terça-feira', 'quarta-feira', 'quinta-feira', 'sexta-feira', 'sábado'];
  return diasSemana[index];
}

function chunkArray(array, chunkSize) {
  const result = [];
  for (let i = 0; i < array.length; i += chunkSize) {
    result.push(array.slice(i, i + chunkSize));
  }
  return result;
}

export default Inicio;
