// Calendario.js

import React from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import interactionPlugin from '@fullcalendar/interaction';

const Calendario = ({ horarios }) => {
  const events = horarios.map((horario) => ({
    id: horario.id,
    title: `Aula de ${horario.desafio} com ${horario.professor}`,
    start: horario.start,
    end: horario.end,
  }));

  return (
    <div className="table-container">
      <h2>Calendário de Eventos</h2>
      <FullCalendar
        className="agenda-container"
        plugins={[dayGridPlugin, interactionPlugin]}
        events={events}
        height="auto"
        locale="pt-br"
        eventContent={({ event }) => (
          <div>
            <p>{event.title}</p>
            <p>{event.start.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })} - {event.end.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</p>
          </div>
        )}
      />
    </div>
  );
};

export default Calendario;
