import React, { useState, useEffect } from 'react';

function CadastroPeriodo() {
  const [numeroPeriodo, setNumeroPeriodo] = useState('');
  const [semestreAno, setSemestreAno] = useState('');
  const [dataInicio, setDataInicio] = useState('');
  const [dataFim, setDataFim] = useState('');
  const [turno, setTurno] = useState([]);
  const [periodos, setPeriodos] = useState([]);

  useEffect(() => {
    try {
      const storedPeriodos = JSON.parse(localStorage.getItem('periodos')) || [];
      setPeriodos(storedPeriodos);
    } catch (error) {
      console.error('Erro ao obter dados do localStorage:', error);
    }
  }, []);

  useEffect(() => {
    try {
      localStorage.setItem('periodos', JSON.stringify(periodos));
    } catch (error) {
      console.error('Erro ao definir dados no localStorage:', error);
    }
  }, [periodos]);

  const adicionarPeriodo = () => {
    if (
      numeroPeriodo.trim() !== '' &&
      semestreAno.trim() !== '' &&
      dataInicio.trim() !== '' &&
      dataFim.trim() !== '' &&
      turno.length > 0
    ) {
      const novoPeriodo = {
        id: new Date().getTime(),
        numeroPeriodo: numeroPeriodo,
        semestreAno: semestreAno,
        dataInicio: dataInicio,
        dataFim: dataFim,
        turno: turno,
      };
      setPeriodos([...periodos, novoPeriodo]);
      setNumeroPeriodo('');
      setSemestreAno('');
      setDataInicio('');
      setDataFim('');
      setTurno([]);
    }
  };

  const excluirPeriodo = (id) => {
    const novosPeriodos = periodos.filter((periodo) => periodo.id !== id);
    setPeriodos(novosPeriodos);
  };

  return (
    <div>
      <div className="form-container">
        <h2>Adicionar Período</h2>
        {/* Coloque os campos de input aqui */}

        <div className="input-container">
          <label>Turno:</label>
          <select
            multiple
            value={turno}
            onChange={(e) => {
              const selectedOptions = Array.from(e.target.selectedOptions, (option) => option.value);
              setTurno(selectedOptions);
            }}
          >
            <option value="matutino">Matutino</option>
            <option value="vespertino">Vespertino</option>
            <option value="noturno">Noturno</option>
          </select>
        </div>

        {/* Remova o bloco de código redundante para o dropdown */}

        <button onClick={adicionarPeriodo}>Adicionar</button>
      </div>

      <div className="table-container">
        <h2>Lista de Períodos</h2>
        <table id="tabelaPessoas">
          {/* Coloque os cabeçalhos da tabela aqui */}
          <tbody>
            {periodos.map((periodo) => (
              <tr key={periodo.id}>
                {/* Coloque as células de dados da tabela aqui */}
                <td>
                  <button className='excluir-btn' onClick={() => excluirPeriodo(periodo.id)}>Excluir</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default CadastroPeriodo;
