import React from "react";


function exibirBoasVindas() {
    // Solicita ao usuário que insira o nome
    var nomeUsuario = prompt("Bem-vindo! Por favor, insira seu nome:");

    // Exibe a mensagem de boas-vindas
    alert("Olá, " + nomeUsuario + "! Bem-vindo à nossa plataforma.");
  }

  // Chama a função de exibição de boas-vindas ao carregar a página
  window.onload = exibirBoasVindas;

export default Inicio;
