import React, { useState, useEffect } from 'react';

function CadastroPeriodo() {
  const [numeroPeriodo, setNumeroPeriodo] = useState('');
  const [semestreAno, setSemestreAno] = useState('');
  const [dataInicio, setDataInicio] = useState('');
  const [dataFim, setDataFim] = useState('');
  const [turno, setTurno] = useState([]);
  const [periodos, setPeriodos] = useState([]);
  const [isTurnoVisible, setIsTurnoVisible] = useState(false);

  useEffect(() => {
    try {
      const storedPeriodos = JSON.parse(localStorage.getItem('periodos')) || [];
      setPeriodos(storedPeriodos);
    } catch (error) {

    }
  }, []);

  useEffect(() => {

    try {
      localStorage.setItem('periodos', JSON.stringify(periodos));
    } catch (error) {

    }
  }, [periodos]);

  const adicionarPeriodo = () => {
    if (
      numeroPeriodo.trim() !== '' &&
      semestreAno.trim() !== '' &&
      dataInicio.trim() !== '' &&
      dataFim.trim() !== '' &&
      turno.length > 0
    ) {
      const novoPeriodo = {
        id: new Date().getTime(),
        numeroPeriodo: numeroPeriodo,
        semestreAno: semestreAno,
        dataInicio: dataInicio,
        dataFim: dataFim,
        turno: turno,
      };



      setPeriodos((prevPeriodos) => { 
        const novoPeriodo = [...prevPeriodos, novoPeriodo]
      localStorage.setItem('periodos', JSON.stringify(novoPeriodo));
      return novoPeriodo;
    });

      setPeriodos([...periodos, novoPeriodo]);
      setNumeroPeriodo('');
      setSemestreAno('');
      setDataInicio('');
      setDataFim('');
      setTurno([]);
      setIsTurnoVisible(false);
    }
  };


  const excluirPeriodo = (id) => {
    const novoPeriodos = periodos.filter((periodo) => periodo.id !== id);
    setPeriodos(novoPeriodos);
  };

  return (
    <div>
      <div className="form-container">
        <h2>Adicionar Período</h2>
        <input
          className='input'
          type="number"
          value={numeroPeriodo}
          onChange={(e) => setNumeroPeriodo(e.target.value)}
          placeholder="Número do Período"
        />
        <input
          type="text"
          value={semestreAno}
          onChange={(e) => setSemestreAno(e.target.value)}
          placeholder="Semestre/Ano do Período"
        />
        <div className="date-input">
          <label>Data de Início:</label>
          <input
            className='periodo-container'
            type="date"
            value={dataInicio}
            onChange={(e) => setDataInicio(e.target.value)}
          />
          <label>Data de Fim:</label>
          <input
            className='periodo-container'
            type="date"
            value={dataFim}
            onChange={(e) => setDataFim(e.target.value)}
          />
        </div>

        <div className="input-container">
          <label>Turno:</label>
          <select
            multiple
            value={turno}
            onChange={(e) => {
              const selectedOptions = Array.from(e.target.selectedOptions, (option) => option.value);
              setTurno(selectedOptions);
            }}
          >
            <option value="matutino">Matutino</option>
            <option value="vespertino">Vespertino</option>
            <option value="noturno">Noturno</option>
          </select>
        </div>


        {isTurnoVisible && (
          <div className="dropdown">
            <select
              multiple
              value={turno}
              onChange={(e) => {
                const selectedOptions = Array.from(e.target.selectedOptions, (option) => option.value);
                setTurno(selectedOptions);
              }}
            >
              <option value="matutino">Matutino</option>
              <option value="vespertino">Vespertino</option>
              <option value="noturno">Noturno</option>
            </select>
          </div>
        )}
        <button onClick={adicionarPeriodo}>Adicionar</button>
      </div>

      <div className="table-container">
        <h2>Lista de Períodos</h2>
        <table id="tabelaPessoas">
          <thead>
            <tr>
              <th>ID</th>
              <th>Número do Período</th>
              <th>Semestre/Ano</th>
              <th>Data de Início</th>
              <th>Data de Fim</th>
              <th>Turno</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {periodos.map((periodo) => (
              <tr key={periodo.id}>
                <td>{periodo.id}</td>
                <td>{periodo.numeroPeriodo}</td>
                <td>{periodo.semestreAno}</td>
                <td>{periodo.dataInicio}</td>
                <td>{periodo.dataFim}</td>
                <td>{periodo.turno.join(', ')}</td>
                <td>
                  <button className='excluir-btn' onClick={() => excluirPeriodo(periodo.id)}>Excluir</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default CadastroPeriodo;
