import React from "react";


// Função para criar e exibir a tela de boas-vindas
function Inicio() {
    // Cria um elemento de div
    var divBoasVindas = document.createElement('div');
    divBoasVindas.id = 'boasVindas';

    // Cria um elemento de h1 e adiciona ao div
    var titulo = document.createElement('h1');
    titulo.textContent = 'Bem-vindo à nossa plataforma!';
    divBoasVindas.appendChild(titulo);

    // Cria um elemento de p e adiciona ao div
    var paragrafo = document.createElement('p');
    paragrafo.textContent = 'Por favor, insira seu nome:';
    divBoasVindas.appendChild(paragrafo);

    // Cria um elemento de input e adiciona ao div
    var inputNome = document.createElement('input');
    inputNome.type = 'text';
    inputNome.id = 'nomeUsuario';
    divBoasVindas.appendChild(inputNome);

    // Cria um botão e adiciona ao div
    var botaoEnviar = document.createElement('button');
    botaoEnviar.textContent = 'Enviar';
    botaoEnviar.addEventListener('click', function () {
      var nomeUsuario = document.getElementById('nomeUsuario').value;
      alert('Olá, ' + nomeUsuario + '! Bem-vindo à nossa plataforma.');
      divBoasVindas.style.display = 'none';
    });
    divBoasVindas.appendChild(botaoEnviar);

    // Adiciona o div à body
    document.body.appendChild(divBoasVindas);
  }

  // Chama a função de exibição de boas-vindas ao carregar a página
  window.onload = Inicio;

export default Inicio;
