import CadastroHorario from "./CadHorario";

function Inicio() {
  return;}

  document.addEventListener('DOMContentLoaded', function () {
    const calendarContainer = document.getElementById('calendar');
    const agendaContainer = document.getElementById('agenda');

    // Inicializa o calendário
    const calendar = new CadastroHorario.Calendar(calendarContainer, {
      initialView: 'dayGridMonth',
      dateClick: handleDateClick,
    });

    calendar.render();

    function handleDateClick(info) {
      const date = info.date;
      const formattedDate = formatDate(date);

      // Simplesmente adiciona a data selecionada à agenda
      addToAgenda(formattedDate);
    }

    function addToAgenda(date) {
      const agendaItem = document.createElement('div');
      agendaItem.classList.add('agenda-item');
      agendaItem.textContent = date;

      agendaContainer.appendChild(agendaItem);
    }

    function formatDate(date) {
      const options = { year: 'numeric', month: 'long', day: 'numeric' };
      return date.toLocaleDateString('en-US', options);
    }
  });


export default Inicio;
