import React, { useState, useEffect } from 'react';

function CadastroHorario() {
  const [selectedProfessor, setSelectedProfessor] = useState('');
  const [selectedPeriodo, setSelectedPeriodo] = useState('');
  const [selectedSala, setSelectedSala] = useState('');
  const [selectedNomeDesafio, setSelectedNomeDesafio] = useState('');
  const [selectedCurso, setSelectedCurso] = useState('');
  const [selectedHorario, setSelectedHorario] = useState('');
  const [selectedHorarioInicial, setSelectedHorarioInicial] = useState('');
  const [selectedHorarioFinal, setSelectedHorarioFinal] = useState('');
  const [selectedData, setSelectedData] = useState('');
  const [professores, setProfessores] = useState([]);
  const [periodos, setPeriodos] = useState([]);
  const [salas, setSalas] = useState([]);
  const [nomeDesafios, setNomeDesafios] = useState([]);
  const [nomeCursos, setCursos] = useState([]);
  const [horarios, setHorarios] = useState([]);
  const [horaInicial, setHoraInicial] = useState([]);
  const [horaFinal, setHoraFinal] = useState([]);


  useEffect(() => {
    const storedHorarios = JSON.parse(localStorage.getItem('Horarios')) || [];
    setHorarios(storedHorarios);

    const storedProfessores = JSON.parse(localStorage.getItem('Professores')) || [];
    setProfessores(storedProfessores);

    const storedPeriodos = JSON.parse(localStorage.getItem('periodos')) || [];
    setPeriodos(storedPeriodos);

    const storedSalas = JSON.parse(localStorage.getItem('Salas')) || [];
    setSalas(storedSalas);

    const storedDesafios = JSON.parse(localStorage.getItem('Desafios')) || [];
    setNomeDesafios(storedDesafios);

    const storedCursos = JSON.parse(localStorage.getItem('cursos')) || [];
    setCursos(storedCursos);

  }, []);

  const adicionarHorarios = () => {
    if (
      selectedProfessor.trim() &&
      selectedNomeDesafio.trim() &&
      selectedPeriodo.trim() &&

      selectedHorarioInicial.trim() &&
      selectedHorarioFinal.trim() &&

      selectedSala.trim()
    ) {
      const novoHorario = {
        id: new Date().getTime(),
        professor: selectedProfessor,
        desafio: selectedNomeDesafio,
        periodo: selectedPeriodo,



        sala: selectedSala,
        horaInicial: selectedHorarioInicial,
        horaFinal: selectedHorarioFinal,
      };
  console.log(novoHorario)
      setHorarios((prevHorarios) => {
        const newHorarios = [...prevHorarios, novoHorario];
        localStorage.setItem('Horarios', JSON.stringify(newHorarios));
        return newHorarios;
      });

      // Limpa os campos apÃ³s adicionar
      setSelectedProfessor('');
      setSelectedNomeDesafio('');
      setSelectedPeriodo('');
      setSelectedCurso('');
      setSelectedHorario('');
      setSelectedHorarioInicial('');
      setSelectedHorarioInicial('');
      setSelectedData('');
      setHoraInicial('');
      setHoraFinal('');
      setSelectedSala('');
    }
  };

  const excluirHorario = (index) => {
    const novoHorario = [...horarios];
    novoHorario.splice(index, 1);
    setHorarios(novoHorario);
    localStorage.setItem('Horario', JSON.stringify(novoHorario))
  };


  return (
    <div>
      <div id='Cad' className="form-container">

        <h4>Selecione um Professor: </h4>
        <select
          id="professor"
          value={selectedProfessor}
          onChange={(e) => setSelectedProfessor(e.target.value)}
        >
          <option value="" disabled>Selecione um professor</option>
          {professores.map((professor, index) => (
            <option key={index} value={professor.nomeProfessor}>{professor.nomeProfessor}</option>
          ))}
        </select>

        <h4>Selecione um Periodo: </h4>
        <select
          id="periodo"
          value={selectedPeriodo}
          onChange={(e) => setSelectedPeriodo(e.target.value)}
        >
          <option value="" disabled>
            Selecione um PerÃ­odo
          </option>
          {periodos.map((periodo, index) => (
            <option key={index} value={periodo.numeroPeriodo}>
              {periodo.numeroPeriodo}
            </option>
          ))}
        </select>

        <h4>Selecione uma Sala: </h4>
        <select
          id="sala"
          value={selectedSala}
          onChange={(e) => setSelectedSala(e.target.value)}
        >
          <option value="" disabled>
            Selecione uma Sala
          </option>
          {salas.map((sala, index) => (
            <option key={index} value={sala.numeroSala}>
              {sala.numeroSala}
            </option>
          ))}
        </select>

        <h4>Selecione um Desafio: </h4>
        <select
          id="desafio"
          value={selectedNomeDesafio}
          onChange={(e) => setSelectedNomeDesafio(e.target.value)}
        >
          <option value="" disabled>
            Selecione um Desafio
          </option>
          {nomeDesafios.map((desafio, index) => (
            <option key={index} value={desafio.nomeDesafio}>
              {desafio.nomeDesafio}
            </option>
          ))}
        </select>
            <div>
        <h4>HorÃ¡rio Inicio: </h4>
        <input
          className='home-container'
          type="time"
          value={horaInicial}
          onChange={(e) => setHoraInicial(e.target.value)}
        />

        <h4>HorÃ¡rio Final: </h4>
        <input
          className='home-container'
          type="time"
          value={horaFinal}
          onChange={(e) => setHoraFinal(e.target.value)}
        />
        </div>

        <button onClick={adicionarHorarios}>Adicionar</button>

      </div>

      <div className="table-container">
        <h2>Horario</h2>
        <table id="tabelaPessoas">
          <thead>
            <tr>
              <th>ID</th>
              <th>Professor</th>
              <th>PerÃ­odo</th>
              <th>Sala</th>
              <th>Desafio</th>
              <th>HorÃ¡rio</th>
              <th>Curso</th>
              <th>Tempo</th>

            </tr>
          </thead>
          <tbody>
            {horarios.map((horario, index) => (
              <tr key={horario.id}>
                <td>{horario.id}</td>
                <td>{horario.professor}</td>
                <td>{horario.periodo}</td>
                <td>{horario.sala}</td>
                <td>{horario.desafio}</td>
                <td>{horario.curso}</td>
                <td>{horario.horario}</td>
                <td>{horario.horaInicial} a{' '}
                  {horario.horaFinal}
                </td>
                <td>
                  <button className="excluir-btn" onClick={() => excluirHorario(horario.id)}>
                    Excluir
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default CadastroHorario;
