import React, { useState, useEffect } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';

function CadastroHorario() {
  const [selectedProfessor, setSelectedProfessor] = useState('');
  const [selectedSala, setSelectedSala] = useState('');
  const [selectedNomeDesafio, setSelectedNomeDesafio] = useState('');
  const [horaInicial, setHoraInicial] = useState('');
  const [horaFinal, setHoraFinal] = useState('');
  const [date, setDate] = useState(new Date());
  const [professores, setProfessores] = useState([]);
  const [salas, setSalas] = useState([]);
  const [nomeDesafios, setNomeDesafios] = useState([]);
  const [horarios, setHorarios] = useState([]);
  const [agenda, setAgenda] = useState([]);

  useEffect(() => {
    const storedHorarios = JSON.parse(localStorage.getItem('Horarios')) || [];
    setHorarios(storedHorarios);

    const storedProfessores = JSON.parse(localStorage.getItem('Professores')) || [];
    setProfessores(storedProfessores);

    const storedSalas = JSON.parse(localStorage.getItem('Salas')) || [];
    setSalas(storedSalas);

    const storedDesafios = JSON.parse(localStorage.getItem('Desafios')) || [];
    setNomeDesafios(storedDesafios);

    const storedAgenda = JSON.parse(localStorage.getItem('Agenda')) || [];
    setAgenda(storedAgenda);
  }, []);

  const adicionarHorario = () => {
    if (
      selectedProfessor.trim() &&
      horaFinal.trim() &&
      horaInicial.trim() &&
      selectedSala.trim()
    ) {
      const novoHorario = {
        id: new Date().getTime(),
        professor: selectedProfessor,
        sala: selectedSala,
        desafio: selectedNomeDesafio,
        horaInicial: horaInicial,
        horaFinal: horaFinal,
      };

      setHorarios((prevHorarios) => {
        const newHorarios = [...prevHorarios, novoHorario];
        localStorage.setItem('Horarios', JSON.stringify(newHorarios));
        return newHorarios;
      });

      const novoEventoAgenda = {
        id: new Date().getTime(),
        title: `Aula de ${selectedNomeDesafio} com ${selectedProfessor}`,
        description: `Horário: ${horaInicial} - ${horaFinal}\nSala: ${selectedSala}`,
        date: date,
      };

      setAgenda((prevAgenda) => {
        const newAgenda = [...prevAgenda, novoEventoAgenda];
        localStorage.setItem('Agenda', JSON.stringify(newAgenda));
        return newAgenda;
      });

      // Limpa os campos após adicionar
      limparCampos();
    }
  };



    const novoEventoAgenda = agenda.filter((evento) => evento.id !== id);
    setAgenda(novoEventoAgenda);
    localStorage.setItem('Agenda', JSON.stringify(novoEventoAgenda));
  };

  const handleDateChange = (novaData) => {
    setDate(novaData);
  };

  const limparCampos = () => {
    setSelectedProfessor('');
    setHoraInicial('');
    setHoraFinal('');
    setSelectedSala('');
  };




      <div className="table-container">
        <h2>Agenda:</h2>
        <Calendar className="table-container" 
          onChange={handleDateChange}
          value={date}
          tileContent={({ date, view }) => {
            if (view === 'month') {
              const eventosNoDia = agenda.filter((evento) => {
                const eventoDate = new Date(evento.date);
                return (
                  eventoDate.getDate() === date.getDate() &&
                  eventoDate.getMonth() === date.getMonth() &&
                  eventoDate.getFullYear() === date.getFullYear()
                );
              });

              return eventosNoDia.length > 0 ? <p>{eventosNoDia.length} eventos</p> : null;
            }
          }}
        />
      </div>




export default CadastroHorario;
