import React, { useState, useEffect } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';

function CadastroHorario() {
    class Evento {
        constructor(titulo, data, descricao) {
          this.titulo = titulo;
          this.data = data;
          this.descricao = descricao;
        }
      }

      class Agenda {
        constructor() {
          this.eventos = [];
        }

        adicionarEvento(evento) {
          this.eventos.push(evento);
        }

        excluirEvento(titulo) {
          this.eventos = this.eventos.filter(evento => evento.titulo !== titulo);
        }

        listarEventos() {
          console.log('Eventos na agenda:');
          this.eventos.forEach(evento => {
            console.log(`Título: ${evento.titulo}, Data: ${evento.data}, Descrição: ${evento.descricao}`);
          });
        }
      }

      // Exemplo de uso da agenda
      const minhaAgenda = new Agenda();

      const evento1 = new Evento('Reunião de Equipe', '2023-12-15', 'Discussão de projetos');
      const evento2 = new Evento('Aniversário', '2023-12-20', 'Festa no escritório');

      minhaAgenda.adicionarEvento(evento1);
      minhaAgenda.adicionarEvento(evento2);

      minhaAgenda.listarEventos();

      // Excluindo um evento
      minhaAgenda.excluirEvento('Reunião de Equipe');

      console.log('\nEventos após excluir:');
      minhaAgenda.listarEventos();
}

export default CadastroHorario;
