import React, { useState, useEffect  } from 'react';

function CadastroEnsalamento() {
  const [selectedProfessor, setSelectedProfessor] = useState('');
  const [selectedPeriodo, setSelectedPeriodo] = useState('');
  const [selectedSala, setSelectedSala] = useState('');
  const [selectedNomeDesafionomeDesafio, setNomeDesafio] = useState('');
  const [curso, setCurso] = useState('');
  const [horario, setHorario] = useState('');
  const [data, setData] = useState('');
  const [horaInicial, setHoraInicial] = useState('');
  const [horaFinal, setHoraFinal] = useState('');
  const [ensalamentos, setEnsalamentos] = useState([]);
  const [professores, setProfessores] = useState([]);
  const [periodos, setPeriodos] = useState([]);
  const [salas, setSalas] = useState([]);
  const [desafios, setDesafios] = useState([]);


  useEffect(() => {
    const storedEnsalamentos = JSON.parse(localStorage.getItem('ensalamentos')) || [];
    setEnsalamentos(storedEnsalamentos);

    const storedProfessores = JSON.parse(localStorage.getItem('Professores')) || [];
    setProfessores(storedProfessores);

    const storedPeriodos = JSON.parse(localStorage.getItem('periodos')) || [];
    setPeriodos(storedPeriodos);

    const storedSalas = JSON.parse(localStorage.getItem('Salas')) || [];
    setSalas(storedSalas);

    const storedDesafios = JSON.parse(localStorage.getItem('Desafios')) || [];
    setDesafios(storedDesafios);

  }, []);

  useEffect(() => {
    localStorage.setItem('ensalamentos', JSON.stringify(ensalamentos));
    console.log('Dados gravados no localStorage:', ensalamentos);
  }, [ensalamentos]);

  const adicionarEnsalamento = () => {
    if (
      selectedProfessor.trim() &&
      selectedNomeDesafio.trim() &&
      selectedPeriodo.trim() &&
      curso.trim() &&
      horario.trim() &&
      data.trim() &&
      horaInicial.trim() &&
      horaFinal.trim() &&
      selectedSala.trim()  ) {

      const novoEnsalamento = {
        id: new Date().getTime(),
        professor: selectedProfessor,
        desafio: selectedNomeDesafio,
        periodo: selectedPeriodo,
        curso: curso,
        horario: horario,
        data: data,
        horaInicial: horaInicial,
        horaFinal: horaFinal,
        sala: selectedSala,
      };

      setEnsalamentos((prevEnsalamentos) => [...prevEnsalamentos, novoEnsalamento]);

      // Limpa os campos após adicionar
      setSelectedProfessor('');
      setSelectedNomeDesafio('');
      setSelectedPeriodo('');
      setCurso('');
      setHorario('');
      setData('');
      setHoraInicial('');
      setHoraFinal('');
      setSelectedSala('');
    }
  };

  const excluirEnsalamento = (id) => {
    setEnsalamentos((prevEnsalamentos) =>
      prevEnsalamentos.filter((ensalamento) => ensalamento.id !== id)
    );
  };


  return (
    <div>
      <div id='Cad' className="form-container">
        <h2>Selecione um:</h2>
        <select 
          id="professor"
          value={selectedProfessor} 
          onChange={(e) => setSelectedProfessor(e.target.value)}
        >
          <option value="" disabled>Selecione um professor</option>
          {professores.map((professor, index) => (
            <option key={index} value={professor.nomeProfessor}>{professor.nomeProfessor}</option>
          ))}
        </select>

        <br></br>
        <select
          id="periodo" 
          value={selectedPeriodo} 
          onChange={(e) => setSelectedPeriodo(e.target.value)}
        >
          <option value="" disabled>
            Selecione um Período
          </option>
          {periodos.map((periodo, index) => (
            <option key={index} value={periodo.numeroPeriodo}>
              {periodo.numeroPeriodo}
            </option>
          ))}
        </select>

        <br></br>
        <select
          id="sala" 
          value={selectedSala} 
          onChange={(e) => setSelectedSala(e.target.value)}
        >
          <option value="" disabled>
            Selecione uma Sala
          </option>
          {salas.map((sala, index) => (
            <option key={index} value={sala.numeroSala}>
              {sala.numeroSala}
            </option>
          ))}
        </select>

        <br></br>
        <select
          id="desafio" 
          value={selectedNomeDesafio} 
          onChange={(e) => setSelectedNomeDesafio(e.target.value)}
        >
          <option value="" disabled>
            Selecione um Desafio
          </option>
          {desafios.map((desafio, index) => (
            <option key={index} value={desafio.numeroPeriodo}>
              {desafio.numeroDesafio}
            </option>
          ))}
        </select>

        <h2>Hora Inicial: </h2>
        <input
          className='home-container'
          type="time"
          value={horaInicial}
          onChange={(e) => setHoraInicial(e.target.value)}
        />

        <h2>Hora Final: </h2>
        <input
          className='home-container'
          type="time"
          value={horaFinal}
          onChange={(e) => setHoraFinal(e.target.value)}
        />

        <div>
          <button onClick={adicionarEnsalamento}>Adicionar</button>
        </div>
      </div>
      <div className="table-container">
        <h2>Ensalamento</h2>
        <table id="tabelaPessoas">
          <thead>
            <tr>
              <th>ID</th>
              <th>Professor</th>
              <th>Desafio</th>
              <th>Período</th>
              <th>Curso</th>
              <th>Horário</th>
              <th>Data - Tempo</th>
              <th>Sala</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {ensalamentos.map((ensalamento) => (
              <tr key={ensalamento.id}>
                <td>{ensalamento.id}</td>
                <td>{ensalamento.professor}</td>
                <td>{ensalamento.periodo}</td>
                <td>{ensalamento.sala}</td>
                <td>{ensalamento.desafio}</td>

                <td>{ensalamento.curso}</td>
                <td>{ensalamento.horario}</td>
                <td>
                  {ensalamento.data} - {ensalamento.horaInicial} a{' '}
                  {ensalamento.horaFinal}
                </td>
                <td>
                  <button className="excluir-btn" onClick={() => excluirEnsalamento(ensalamento.id)}>
                    Excluir
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default CadastroEnsalamento;
