import React, { useState, useEffect } from 'react';

function CadastroPeriodo() {
  const [numeroPeriodo, setNumeroPeriodo] = useState('');
  const [semestreAno, setSemestreAno] = useState('');
  const [dataInicio, setDataInicio] = useState('');
  const [dataFim, setDataFim] = useState('');
  const [turno, setTurno] = useState([]);
  const [periodos, setPeriodos] = useState([]);
  const [isTurnoVisible, setIsTurnoVisible] = useState(false);

  useEffect(() => {
    try {
      const storedPeriodos = JSON.parse(localStorage.getItem('periodos')) || [];
      setPeriodos(storedPeriodos);
    } catch (error) {
      // Tratar erros ao fazer o parsing do JSON
      console.error(error);
    }
  }, []);

  useEffect(() => {
    try {
      localStorage.setItem('periodos', JSON.stringify(periodos));
    } catch (error) {
      // Tratar erros ao armazenar no localStorage
      console.error(error);
    }
  }, [periodos]);

  const adicionarPeriodo = () => {
    if (
      numeroPeriodo.trim() !== '' &&
      semestreAno.trim() !== '' &&
      dataInicio.trim() !== '' &&
      dataFim.trim() !== '' &&
      turno.length > 0
    ) {
      const novoPeriodo = {
        id: new Date().getTime(),
        numeroPeriodo: numeroPeriodo,
        semestreAno: semestreAno,
        dataInicio: dataInicio,
        dataFim: dataFim,
        turno: turno,
      };

      setPeriodos((prevPeriodos) => [...prevPeriodos, novoPeriodo]);
      localStorage.setItem('periodos', JSON.stringify([...periodos, novoPeriodo]));

      setNumeroPeriodo('');
      setSemestreAno('');
      setDataInicio('');
      setDataFim('');
      setTurno([]);
      setIsTurnoVisible(false);
    }
  };

  const excluirPeriodo = (id) => {
    const novosPeriodos = periodos.filter((periodo) => periodo.id !== id);
    setPeriodos(novosPeriodos);
  };

  return (
    <div>
      <div className="form-container">
        <h2>Adicionar Período</h2>
        {/* Restante do código permanece inalterado */}
        {/* ... */}
      </div>

      <div className="table-container">
        <h2>Lista de Períodos</h2>
        <table id="tabelaPessoas">
          {/* Restante do código permanece inalterado */}
          {/* ... */}
        </table>
      </div>
    </div>
  );
}

export default CadastroPeriodo;
