import React, { useState, useEffect } from 'react';
import Agenda from './pages/Agenda.js';

function CadDesafio() {
  const [nomeDesafio, setNomeDesafio] = useState('');
  const [selectedPeriodo, setSelectedPeriodo] = useState('');
  const [selectedSala, setSelectedSala] = useState('');
  const [selectedProfessor, setSelectedProfessor] = useState('');
  const [dataInicio, setDataInicio] = useState('');
  const [dataFim, setDataFim] = useState('');
  const [horaInicial, setHoraInicial] = useState('');
  const [horaFinal, setHoraFinal] = useState('');
  const [selectedDiaSemana, setSelectedDiaSemana] = useState('');
  const [desafios, setDesafios] = useState([]);
  const [professores, setProfessores] = useState([]);
  const [periodos, setPeriodos] = useState([]);
  const [salas, setSalas] = useState([]);
  const [editingIndex, setEditingIndex] = useState(null);

  useEffect(() => {
    const storedDesafios = JSON.parse(localStorage.getItem('Desafios')) || [];
    setDesafios(storedDesafios);

    const storedProfessores = JSON.parse(localStorage.getItem('Professores')) || [];
    setProfessores(storedProfessores);

    const storedPeriodos = JSON.parse(localStorage.getItem('periodos')) || [];
    setPeriodos(storedPeriodos);

    const storedSalas = JSON.parse(localStorage.getItem('Salas')) || [];
    setSalas(storedSalas);
  }, []);

  const adicionarDesafio = () => {
    if (
      nomeDesafio.trim() !== '' &&
      selectedPeriodo.trim() !== '' &&
      selectedProfessor.trim() !== '' &&
      dataInicio.trim() !== '' &&
      dataFim.trim() !== '' &&
      horaFinal.trim() !== '' &&
      horaInicial.trim() !== '' &&
      selectedSala.trim() !== '' &&
      selectedDiaSemana.trim() !== ''
    ) {
      const novoDesafio = {
        nomeDesafio,
        periodo: selectedPeriodo,
        professor: selectedProfessor,
        dataInicio,
        dataFim,
        horaInicial,
        horaFinal,
        sala: selectedSala,
        diaSemana: selectedDiaSemana,
      };

      if (editingIndex === null) {
        setDesafios((prevDesafios) => {
          const newDesafios = [...prevDesafios, novoDesafio];
          localStorage.setItem('Desafios', JSON.stringify(newDesafios));
          return newDesafios;
        });
      } else {
        const desafiosAtualizados = [...desafios];
        desafiosAtualizados[editingIndex] = novoDesafio;

        setDesafios(desafiosAtualizados);
        localStorage.setItem('Desafios', JSON.stringify(desafiosAtualizados));
        setEditingIndex(null);
      }

      setNomeDesafio('');
      setSelectedPeriodo('');
      setSelectedProfessor('');
      setDataInicio('');
      setDataFim('');
      setHoraInicial('');
      setHoraFinal('');
      setSelectedSala('');
      setSelectedDiaSemana('');
    }
  };

  const excluirDesafio = (index) => {
    const novoDesafio = [...desafios];
    novoDesafio.splice(index, 1);
    setDesafios(novoDesafio);
    localStorage.setItem('Desafios', JSON.stringify(novoDesafio));
  };

  const editarDesafio = (index) => {
    setEditingIndex(index);
    const desafioSelecionado = desafios[index];
    setNomeDesafio(desafioSelecionado.nomeDesafio);
    setSelectedPeriodo(desafioSelecionado.periodo);
    setSelectedProfessor(desafioSelecionado.professor);
    setDataInicio(desafioSelecionado.dataInicio);
    setDataFim(desafioSelecionado.dataFim);
    setHoraInicial(desafioSelecionado.horaInicial);
    setHoraFinal(desafioSelecionado.horaFinal);
    setSelectedSala(desafioSelecionado.sala);
    setSelectedDiaSemana(desafioSelecionado.diaSemana);
  };

  return (
    <div>
      <div id='Cad' className="form-container">
        <h1>Cadastro de Desafios</h1>
        <h4>{editingIndex === null ? 'Adicionar:' : 'Editar'} </h4>
        <input
          type="text"
          id="nomeDesafio"
          placeholder="Nome da Matéria"
          value={nomeDesafio}
          onChange={(e) => setNomeDesafio(e.target.value)}
        />
        <p></p>
        <select
          id="periodo"
          value={selectedPeriodo}
          onChange={(e) => setSelectedPeriodo(e.target.value)}
        >
          <option value="" disabled>
            Selecione um período
          </option>
          {periodos.map((periodo, index) => (
            <option key={index} value={periodo.numeroPeriodo}>
              {periodo.numeroPeriodo}
            </option>
          ))}
        </select>

        <p></p>

        <select
          id="professor"
          value={selectedProfessor}
          onChange={(e) => setSelectedProfessor(e.target.value)}
        >
          <option value="" disabled>
            Selecione um professor
          </option>
          {professores.map((professor, index) => (
            <option key={index} value={professor.nomeProfessor}>
              {professor.nomeProfessor}
            </option>
          ))}
        </select>

        <p></p>
        <label>Data de Inicio:</label>
        <br></br>
        <input
          type="date"
          id="dataIncio"
          value={dataInicio}
          onChange={(e) => setDataInicio(e.target.value)}
        />
        <p></p>
        <label>Data de Fim:</label>
        <br></br>
        <input
          type="date"
          id="dataFim"
          value={dataFim}
          onChange={(e) => setDataFim(e.target.value)}
        />
        <p></p>

        <p></p>
        <div>
          <h4>Horário Inicio: </h4>
          <input
            className="home-container"
            type="time"
            value={horaInicial}
            onChange={(e) => setHoraInicial(e.target.value)}
          />
          <p></p>
          <h4>Horário Final: </h4>
          <input
            className="home-container"
            type="time"
            value={horaFinal}
            onChange={(e) => setHoraFinal(e.target.value)}
          />
        </div>
        <p></p>
        <h4>Selecione uma Sala: </h4>
        <select
          id="sala"
          value={selectedSala}
          onChange={(e) => setSelectedSala(e.target.value)}
        >
          <option value="" disabled>
            Selecione uma Sala
          </option>

          {salas.map((sala, index) => (
            <option key={index} value={sala.numeroSala}>
              {sala.numeroSala}
            </option>
          ))}
        </select>

        <p></p>
        <label>Dia da Semana:</label>
        <br />
        <select
          id="diaSemana"
          value={selectedDiaSemana}
          onChange={(e) => setSelectedDiaSemana(e.target.value)}
        >
          <option value="" disabled>
            Selecione um dia da semana
          </option>
          <option value="Segunda">Segunda-feira</option>
          <option value="Terca">Terça-feira</option>
          <option value="Quarta">Quarta-feira</option>
          <option value="Quinta">Quinta-feira</option>
          <option value="Sexta">Sexta-feira</option>
          <option value="Sabado">Sábado</option>
          <option value="Domingo">Domingo</option>
        </select>

        <p></p>
        <button onClick={adicionarDesafio}>
          {editingIndex === null ? 'Adicionar' : 'Atualizar'}
        </button>
      </div>

      <Agenda desafios={desafios} />
      <div className="table-container">
        <h2>Lista de Desafios</h2>
        <table id="tabelaDesafios">
          <thead className="trtable-container">
            <tr>
              <th>ID</th>
              <th>Matéria</th>
              <th>Período</th>
              <th>Professor</th>
              <th>Data Inicio</th>
              <th>Data Fim</th>
              <th>Dia da Semana</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody className="trtable-container">
            {desafios.map((desafio, index) => (
              <tr key={index}>
                <td>{index + 1}</td>
                <td>{desafio.nomeDesafio}</td>
                <td>{desafio.periodo}</td>
                <td>{desafio.professor}</td>
                <td>{desafio.dataInicio}</td>
                <td>{desafio.dataFim}</td>
                <td>{desafio.diaSemana}</td>
                <td>
                  <button
                    className="alterar-btn"
                    onClick={() => editarDesafio(index)}
                  >
                    Editar
                  </button>
                  <button
                    className="excluir-btn"
                    onClick={() => excluirDesafio(index)}
                  >
                    Excluir
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );


}



export default CadDesafio;
