import React, { useState, useEffect } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';

function CadastroHorario() {
    document.addEventListener('DOMContentLoaded', function () {
        const eventos = [];

        const calendario = new FullCalendar.Calendar(document.getElementById('calendario'), {
          initialView: 'dayGridMonth',
          events: eventos,
          dateClick: function (info) {
            const titulo = prompt('Digite o título do evento:');
            if (titulo) {
              const novoEvento = {
                title: titulo,
                date: info.dateStr,
              };
              calendario.addEvent(novoEvento);
              eventos.push(novoEvento);
              atualizarListaEventos();
            }
          },
          eventClick: function (info) {
            const confirmacao = confirm(`Deseja excluir o evento "${info.event.title}"?`);
            if (confirmacao) {
              calendario.getEventById(info.event.id).remove();
              eventos.splice(eventos.findIndex(e => e.id === info.event.id), 1);
              atualizarListaEventos();
            }
          },
        });

        calendario.render();

        function atualizarListaEventos() {
          const listaEventos = document.getElementById('lista-eventos');
          listaEventos.innerHTML = '';

          eventos.forEach(evento => {
            const li = document.createElement('li');
            li.textContent = `${evento.title} - ${evento.date}`;
            listaEventos.appendChild(li);
          });
        }
      });

}

export default CadastroHorario;
