import React, { useState, useEffect } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import interactionPlugin from '@fullcalendar/interaction';

function Agenda({ desafios }) {
  const [horarios, setHorarios] = useState([]);

  useEffect(() => {
    const eventos = desafios.map((desafio, index) => ({
      title: desafio.nomeDesafio,
      start: desafio.dataInicio + 'T' + desafio.horaInicial,
      end: desafio.dataFim + 'T' + desafio.horaFinal,
      extendedProps: {
        professor: desafio.professor,
        sala: desafio.sala,
        desafio: desafio.nomeDesafio,
        horaInicial: desafio.horaInicial,
        horaFinal: desafio.horaFinal,
        diaSemana: desafio.selectedDiaSemana,
      },
    }));

    setHorarios(eventos);
  }, [desafios]);

  return (
    <div>
      <div className="agenda-container">
        <h2 className="agenda-container1">Calendário de Aulas</h2>
        <FullCalendar
          plugins={[dayGridPlugin, interactionPlugin]}
          events={horarios}
          height="auto"
          locale="pt-br"
          eventContent={renderizarConteudoEvento}
          classNames="evento-customizado"
        />
      </div>
    </div>
  );
}

const renderizarConteudoEvento = (arg) => {
  const { title, extendedProps } = arg.event;

  return {
    html: `
      <br className="agenda-container">${title}</b><br >
      Professor: ${extendedProps.professor}<br>
      Sala: ${extendedProps.sala}<br>
      Desafio: ${extendedProps.desafio}<br>
      Horário: ${extendedProps.horaInicial} - ${extendedProps.horaFinal}<br>
      Dia da Semana: ${extendedProps.diaSemana}
    `,
  };
};

export default Agenda;
