import horario from './CadHorario';


function Calendario() {

const diasDoMes = Array.from({ length: 30 }, (_, index) => index + 1);

  const Horario = JSON.parse(localStorage.getItem('horarios')) || [];


  return (
    <div>
      <h2>Novembro 2023</h2>
      <table>
        <thead>
          <tr className="semana">
            <th>Dom</th>
            <th>Seg</th>
            <th>Ter</th>
            <th>Qua</th>
            <th>Qui</th>
            <th>Sex</th>
            <th>Sáb</th>
          </tr>
        </thead>
        <tbody>
          {chunkArray(diasDoMes, 7).map((semana, index) => (
            <tr key={index} className="dias">
              {semana.map((dia, diaIndex) => (
                <td key={diaIndex}>
                  {dia}
                  <ul>
                    {horario
                      .filter((horario) => horario.diaDaSemana === getDiaSemana(diaIndex))
                      .map((horario) => (
                        <li key={horario.id}>
                          <strong>Horário:</strong> {horario.horario} - {' '}
                          <strong>Professor:</strong> {horario.professor} - {' '}
                          <strong>Sala:</strong> {horario.sala}
                        </li>
                      ))}
                  </ul>
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

function getDiaSemana(index) {
  const diasSemana = ['domingo', 'segunda-feira', 'terça-feira', 'quarta-feira', 'quinta-feira', 'sexta-feira', 'sábado'];
  return diasSemana[index];
}

function chunkArray(array, chunkSize) {
  const result = [];
  for (let i = 0; i < array.length; i += chunkSize) {
    result.push(array.slice(i, i + chunkSize));
  }
  return result;
}


export default Calendario;
