import React, { useState, useEffect  } from 'react';
import nomeProfessor from '../pages/CadProfessor';


function CadastroEnsalamento() {
  const [nomeProfessor, setNomeProfessor] = useState('');
  const [desafio, setDesafio] = useState('');
  const [periodo, setPeriodo] = useState('');
  const [curso, setCurso] = useState('');
  const [horario, setHorario] = useState('');
  const [data, setData] = useState('');
  const [horaInicial, setHoraInicial] = useState('');
  const [horaFinal, setHoraFinal] = useState('');
  const [sala, setSala] = useState('');
  const [ensalamentos, setEnsalamentos] = useState([]);

  useEffect(() => {
    const storedEnsalamentos = JSON.parse(localStorage.getItem('ensalamentos')) || [];
    setEnsalamentos(storedEnsalamentos);
  }, []);

  const adicionarEnsalamento = () => {
    console.log('Antes da adição:', ensalamentos);
    if (
      professor.trim() &&
      desafio.trim() &&
      periodo.trim() &&
      curso.trim() &&
      horario.trim() &&
      data.trim() &&
      horaInicial.trim() &&
      horaFinal.trim() &&
      sala.trim()
    ) {
      const novoEnsalamento = {
        id: new Date().getTime(),
        professor: professor,
        desafio: desafio,
        periodo: periodo,
        curso: curso,
        horario: horario,
        data: data,
        horaInicial: horaInicial,
        horaFinal: horaFinal,
        sala: sala,
      };

      const novosEnsalamentos = [...ensalamentos, novoEnsalamento];

      // Gravação síncrona no localStorage
      localStorage.setItem('ensalamentos', JSON.stringify(novosEnsalamentos));

      // Atualiza o estado após a gravação
      setEnsalamentos(novosEnsalamentos);

      // Limpa os campos após adicionar
      setProfessor('');
      setDesafio('');
      setPeriodo('');
      setCurso('');
      setHorario('');
      setData('');
      setHoraInicial('');
      setHoraFinal('');
      setSala('');

      // Logs para verificar
      console.log('Dados gravados:', novosEnsalamentos);
    }
  };

  const excluirEnsalamento = (id) => {
    const novosEnsalamentos = ensalamentos.filter((ensalamento) => ensalamento.id !== id);

    // Gravação síncrona no localStorage
    localStorage.setItem('ensalamentos', JSON.stringify(novosEnsalamentos));

    // Atualiza o estado após a gravação
    setEnsalamentos(novosEnsalamentos);

    // Logs para verificar
    console.log('Dados gravados:', novosEnsalamentos);
  };

  return (
    <div>
      <div className="form-container">
        <h2>Selecione o Professor</h2>
        <select
          className='home-container'
          value={professor}
          onChange={(e) => setProfessor(e.target.value)}
        >
          <option value="professor1">Professor 1</option>
          <option value="professor2">Professor 2</option>
        </select>

        <h2>Cadastro de Período</h2>
        <select
          className='home-container'
          value={periodo}
          onChange={(e) => setPeriodo(e.target.value)}
        >
          <option value="periodo1">Período 1</option>
          <option value="periodo2">Período 2</option>
        </select>

        <h2>Cadastro de Salas</h2>
        <select
          className='home-container'
          value={sala}
          onChange={(e) => setSala(e.target.value)}
        >
          <option value="sala1">Sala 1</option>
          <option value="sala2">Sala 2</option>
        </select>

        <h2>Cadastro de Matérias</h2>
        <select
          className='home-container'
          value={desafio}
          onChange={(e) => setDesafio(e.target.value)}
        >
          <option value="desafio1">Desafio 1</option>
          <option value="desafio2">Desafio 2</option>
        </select>

        <h2>Hora Inicial: </h2>
        <input
          className='home-container'
          type="time"
          value={horaInicial}
          onChange={(e) => setHoraInicial(e.target.value)}
        />

        <h2>Hora Final: </h2>
        <input
          className='home-container'
          type="time"
          value={horaFinal}
          onChange={(e) => setHoraFinal(e.target.value)}
        />

        <div>
          <button onClick={adicionarEnsalamento}>Adicionar</button>
        </div>
      </div>
      <div className="table-container">
        <h2>Ensalamento</h2>
        <table id="tabelaPessoas">
          <thead>
            <tr>
              <th>ID</th>
              <th>Professor</th>
              <th>Desafio</th>
              <th>Período</th>
              <th>Curso</th>
              <th>Horário</th>
              <th>Data - Tempo</th>
              <th>Sala</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {ensalamentos.map((ensalamento) => (
              <tr key={ensalamento.id}>
                <td>{ensalamento.id}</td>
                <td>{ensalamento.professor}</td>
                <td>{ensalamento.desafio}</td>
                <td>{ensalamento.periodo}</td>
                <td>{ensalamento.curso}</td>
                <td>{ensalamento.horario}</td>
                <td>
                  {ensalamento.data} - {ensalamento.horaInicial} a{' '}
                  {ensalamento.horaFinal}
                </td>
                <td>{ensalamento.sala}</td>
                <td>
                  <button className="excluir-btn" onClick={() => excluirEnsalamento(ensalamento.id)}>
                    Excluir
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default CadastroEnsalamento;
