import React, { useState, useEffect } from 'react';

const CadPessoa = () => {
  const [nome, setNome] = useState('');
  const [dataInicio, setDataInicio] = useState('');
  const [nomeCoordenador, setNomeCoordenador] = useState('');
  const [cadPessoa, setCadPessoa] = useState([]);
  const [editingIndex, setEditingIndex] = useState(null);

  useEffect(() => {
    const storedCadPessoa = JSON.parse(localStorage.getItem('pessoa')) || [];
    setCadPessoa(storedCadPessoa);
  }, []);

  const adicionarCurso = () => {
    if (nome.trim() !== '' && dataInicio && nomeCoordenador.trim() !== '') {
      const newCadPessoa = {
        id: new Date().getTime(),
        nome: nome,
        dataInicio: dataInicio,
        nomeCoordenador: nomeCoordenador,
      };

      setCadPessoa((prevCadPessoa) => [...prevCadPessoa, newCadPessoa]);
      localStorage.setItem('cadPessoa', JSON.stringify([...cadPessoa, newCadPessoa]));

      setNome('');
      setDataInicio('');
      setNomeCoordenador('');
    }
  };

  const excluirCurso = (id) => {
    const updatedCadPessoa = CadPessoa.filter((curso) => curso.id !== id);
    setCadPessoa(updatedCadPessoa);
    localStorage.setItem('cadpessoa', JSON.stringify(updatedCadPessoa));
  };

  const updateCurso = () => {
    if (nome.trim() !== '' && dataInicio && nomeCoordenador.trim() !== '') {
      const updatedCurso = {
        id: cadPessoa[editingIndex].id,
        nome: nome,
        dataInicio: dataInicio,
        nomeCoordenador: nomeCoordenador,
      };

      const novosCadPessoa = [...cadPessoa];
      novosCadPessoa[editingIndex] = updatedCurso;

      setCadPessoa(novosCadPessoa);
      localStorage.setItem('cadPessoa', JSON.stringify(novosCadPessoa));

      // Limpa os campos após a atualização
      setEditingIndex(null);
      setNome('');
      setDataInicio('');
      setNomeCoordenador('');
    }
  };

  const iniciarEdicao = (index) => {
    setEditingIndex(index);
    const cadPessoaEditando = cadPessoa[index];
    setNome(cadPessoaEditando.nome);
    setDataInicio(cadPessoaEditando.dataInicio);
    setNomeCoordenador(cadPessoaEditando.nomeCoordenador);
  };

  return (
    <div>
      <div id='Cad' className="form-container">
        <h1>Cadastro de Pessoas</h1>
        <h4>Adicionar:</h4>
        <div>
          <input
            type="text"
            id="nomePessoa"
            placeholder="Nome da Pessoa"
            value={nome}
            onChange={(e) => setNome(e.target.value)}
          />
          <input
            type="text"
            id="matPessoa"
            placeholder="Matricula da Pessoa"
            value={nome}
            onChange={(e) => setNome(e.target.value)}
          />
          <input
            type="text"
            id="nomeCurso"
            placeholder="Nome do curso"
            value={nome}
            onChange={(e) => setNome(e.target.value)}
          />

          <p></p>
          {editingIndex === null ? (
            <button onClick={adicionarCurso}>Adicionar</button>
          ) : (
            <button onClick={updateCurso}>Atualizar</button>
          )}
        </div>
      </div>

      <div className="table-container">
        <h2>Lista de Pessoas</h2>
        <table id="tabelaPessoas" className="trtable-container">
          <thead>
            <tr>
              <th>ID</th>
              <th>Nome</th>

              <th>Ações</th>
            </tr>
          </thead>
          <tbody id="corpoTabela" className="trtable-container">
            {cadPessoa.map((curso, index) => (
              <tr key={curso.id}>
                <td>{curso.id}</td>
                <td>{curso.nome}</td>

                <td>

                  <button className="alterar-btn" onClick={() => iniciarEdicao(index)}> Editar </button>
                  <button className="excluir-btn" onClick={() => excluirCurso(curso.id)}> Excluir </button>

                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default CadPessoa;
