import React from "react";

var nomeUsuario = prompt("Por favor, insira seu nome:");

// Exibe uma mensagem de boas-vindas
if (nomeUsuario) {
  alert("Bem-vindo, " + nomeUsuario + "!");
} else {
  alert("Bem-vindo!");
}
export default Inicio;
