function Inicio() {


  const diasDoMes = Array.from({ length: 30 }, (_, index) => index + 1);

  localStorage.setItem('Horario', JSON.stringify(novoHorario))

  return (
    <div>
      <h2>Novembro de 2023</h2>
      <table>
        <thead>
          <tr>
            <th>Dom</th>
            <th>Seg</th>
            <th>Ter</th>
            <th>Qua</th>
            <th>Qui</th>
            <th>Sex</th>
            <th>Sáb</th>
          </tr>
        </thead>
        <tbody>
          {chunkArray(diasDoMes, 7).map((semana, index) => (
            <tr key={index}>
              {semana.map((dia, diaIndex) => (
                <td key={diaIndex}>
                  {dia}
                  <ul>
                    {novoHorario
                      .filter((novoHorario) => novoHorario.diaDaSemana === getDiaSemana(diaIndex))
                      .map((novoHorario) => (
                        <li key={novoHorario.id}>
                          <td>{novoHorario.id}</td>
                          <td>{novoHorario.professor}</td>
                          <td>{novoHorario.sala}</td>
                          <td>{novoHorario.desafio}</td>
                          <td>{novoHorario.horaInicial} a{' '}
                            {novoHorario.horaFinal}
                          </td>
                        </li>
                      ))}
                  </ul>
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

function getDiaSemana(index) {
  const diasSemana = ['domingo', 'segunda-feira', 'terça-feira', 'quarta-feira', 'quinta-feira', 'sexta-feira', 'sábado'];
  return diasSemana[index];
}

function chunkArray(array, chunkSize) {
  const result = [];
  for (let i = 0; i < array.length; i += chunkSize) {
    result.push(array.slice(i, i + chunkSize));
  }
  return result;
}

export default Inicio;
